import React from 'react'
import { ColorConsumer } from '../contexts/color'

const colors = ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet']

const SelectColors = () => {
  return (
    <div>
      <h2>색상을 선택하시오.</h2>
      <div style={{ display: 'flex' }}>
        {colors.map(color => {
          return (
            <div
              key={color}
              style={{
                background: color,
                width: '24px',
                height: '24px',
                cursor: 'pointer',
              }}
            ></div>
          )
        })}
      </div>
      <hr />
    </div>
  )
}

export default SelectColors
